          _this7.content = data.content;
          var leaveStandardApply = JSON.parse(window.localStorage.getItem('leaveStandardApply'));

          if (leaveStandardApply) {
            if (leaveStandardApply.version < data.version) {
              _this7.open();
            }
          } else {
            _this7.open();
          }

          window.localStorage.setItem('leaveStandardApply', JSON.stringify(data));
        }
      });
    },
