import { getUserList } from '../../../api/admin/user.js';
import { mapGetters } from 'vuex';
export default {
    data() {
        return {
            query: {
                userId: null,
                status: '',
                likeKeyWords: '',
                current: 1,
                size: 10
            },
            list: [],
            listType: '1',
            pages: 0,
            total: 0,
            disabled: false,
            listLoading: false,
            statusOptions: [
                {
                    value: '',
                    label: '全部'
                },
                {
                    value: '0',
                    label: '审批中'
                },
                {
                    value: '1',
                    label: '同意'
                },
                {
                    value: '2',
                    label: '拒绝'
                }
            ],
            classifyOptions: [
                {
                    value: '',
                    label: '全部'
                }
            ],
            userOptions: []
        };
    },
    created() {
        this.query.userId = this.userId;
        this.getUserList();
    },
    computed: {
        ...mapGetters(['permissions', 'userId'])
    },
    methods: {
        openList(val) {
            if (val == 2) {
                this.$router.push({
                    path: '/purchase/purchaseApply/approver'
                });
            }
        },

        getUserList() {
            getUserList().then(response => {
                console.log(response);
                response.data.data.forEach(element => {
                    //console.log(element)
                    this.userOptions.push({
                        value: element.userId,
                        label: element.username
                    });
                });
            });
        },

        handleSizeChange(val) {
            this.query.size = val;
            this.getInvoicePage();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            sessionStorage.setItem('page2', val);
            this.getInvoicePage();
        }
    }
    // computed: {
    //     ...mapGetters(['permissions', 'userId'])
    // }
};
