import {
    fetchMenuTree,
    addObj,
    updateObj,
    deleteObj
} from "../../api/other/menu.js";
import { mapGetters } from "vuex";
export default {
    data() {
        return {
            dialogTitle: "",
            listLoading: false,
            dialogVisible: false,
            saving: false,
            list: [],
            role_btn_add: false,
            role_btn_edit: false,
            role_btn_del: false,
            formData: {
                newData: true,
                parentId: "",
                menuId: "",
                name: "",
                type: "",
                path: "",
                icon: "",
                sort: ""
            },
            rules: {
                menuId: [
                    { required: true, message: "请输入节点ID", trigger: "blur" }
                ],
                name: [
                    { required: true, message: "请输入标题", trigger: "blur" }
                ],
                type: [
                    { required: true, message: "请选择类型", trigger: "blur" }
                ]
            }
        };
    },
    created() {
        this.getRoleList();
        this.role_btn_add = this.permissions["sys_role_add"];
        this.role_btn_edit = this.permissions["sys_role_edit"];
        this.role_btn_del = this.permissions["sys_role_del"];
    },
    computed: {
        ...mapGetters(["permissions"])
    },
    methods: {
        getRoleList() {
            this.loading = true;
            fetchMenuTree()
                .then(response => {
                    this.loading = false;
                    this.list = response.data.data;
                    console.log(this.list);
                })
                .catch(() => {
                    this.loading = false;
                });
        },
        handleModalAdd(data) {
            this.dialogTitle = "添加菜单";
            this.formData.newData = true;
            this.formData.parentId = data.id;
            this.formData.menuId = "";
            this.formData.name = "";
            this.formData.type = "";
            this.formData.path = "";
            this.formData.icon = "";
            this.formData.sort = "";
            this.dialogVisible = true;
        },
        handleModalEdit(data) {
            this.dialogTitle = "编辑菜单 - " + data.name;
            this.formData.newData = false;
            this.formData.parentId = data.parentId;
            this.formData.menuId = data.id;
            this.formData.name = data.name;
            this.formData.type = data.type;
            this.formData.path = data.path;
            this.formData.icon = data.icon;
            this.formData.sort = data.sort;
            this.dialogVisible = true;
        },
        handleSizeChange(val) {
            this.query.size = val;
            this.getPageList();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            this.getPageList();
        },
        handleSubmit() {
            this.$refs["formData"].validate(valid => {
                if (valid) {
                    this.saving = true;
                    if (!this.formData.newData) {
                        updateObj(this.formData)
                            .then(res => {
                                if (res.data.data) {
                                    this.dialogVisible = false;
                                    this.getRoleList();
                                }
                            })
                            .finally(() => {
                                this.saving = false;
                            });
                    } else {
                        addObj(this.formData)
                            .then(res => {
                                if (res.data.data) {
                                    this.dialogVisible = false;
                                    this.getRoleList();
                                }
                            })
                            .finally(() => {
                                this.saving = false;
                            });
                    }
                }
            });
        },
        // 删除
        handleDel(index, row) {
            console.log(row);
            if (row.id) {
                this.$confirm("确认删除 " + row.name + " 吗?", "提示", {
                    type: "warning"
                }).then(() => {
                    deleteObj(row.id).then(res => {
                        if (res.data.data) {
                            this.getRoleList();
                        }
                    });
                });
            }
        }
    },
    mounted() {}
};
