import { getReceiptApproverPage } from '../../../api/apply/receipt.js';
// import {getUserInfo} from "../../../api/admin/user.js";
import { mapGetters } from 'vuex';
export default {
    data() {
        return {
            query: {
                userId: null,
                status: '',
                current: 1,
                size: 10
            },
            statusOptions: [
                {
                    value: '',
                    label: '全部'
                },
                {
                    value: '0',
                    label: '审批中'
                },
                {
                    value: '1',
                    label: '同意'
                },
                {
                    value: '2',
                    label: '拒绝'
                }
            ],
            pages: 0,
            total: 0,
            listLoading: false,
            list: [],
            number: '',
            listType: '2'
        };
    },
    created() {
        window.localStorage.removeItem('editReceiptInfo');
        this.query.status = this.$route.query.status;
        if (!this.query.status) {
            this.query.status = '';
        }
        this.query.userId = this.userId;
        this.getReceiptApproverPage();
    },
    computed: {
        ...mapGetters(['permissions', 'userId'])
    },
    methods: {
        sortChange(a, b) {
            //排序
            return a.priceYuan - b.priceYuan;
        },
        getReceiptApproverPage() {
            this.listLoading = true;
            getReceiptApproverPage(this.query)
                .then(response => {
                    this.listLoading = false;
                    this.total = response.data.data.total;
                    this.query.current = response.data.data.current;
                    this.query.size = response.data.data.size;
                    this.list = response.data.data.records;
                })
                .catch(() => {
                    this.listLoading = false;
                });
        },
        handleSizeChange(val) {
            this.query.size = val;
            this.getReceiptApproverPage();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            this.getReceiptApproverPage();
        },
        handInfo(data) {
            this.$router.push({
                path: '/apply/receipt/info/' + data.receiptId
            });
        },
        openList(val) {
            if (val == 1) {
                this.$router.push({
                    path: '/apply/receipt'
                });
            }
        }
    },
    mounted() {}
};
