import { getStandardApplyInfoById } from "../../../api/standard/apply.js";
import { mapGetters } from "vuex";
export default {
    data() {
        return {
            query: {
                itemId: null,
                userId: null,
                deptId: null
            },
            formData: {
                newData: true,
                require: "",
            },
            saving: false,
            rules: {
                require: [{ required: true, message: "请填写事由" }]
            },
            levelptions: [
                {
                    label: "1级",
                    value: "1"
                },
                {
                    label: "2级",
                    value: "2"
                },
                {
                    label: "3级",
                    value: "3"
                },
                {
                    label: "4级",
                    value: "4"
                },
                {
                    label: "5级",
                    value: "5"
                },
                {
                    label: "6级",
                    value: "6"
                },
                {
                    label: "7级",
                    value: "7"
                },
                {
                    label: "8级",
                    value: "8"
                }
            ]
        };
    },
    created() {

    },
    computed: {
        ...mapGetters(["permissions", "userId"])
    },
    methods: {
        backHistory() {
            this.$router.go(-1);
        },
        onSubmit() {
            this.$refs["formData"].validate(valid => {
                if (valid) {
                    this.saving = true;
                    if (!this.formData.newData) {
                        editObj(this.formData)
                            .then(res => {
                                if (res.data.data) {
                                    this.backHistory();
                                }
                            })
                            .finally(() => {
                                this.saving = false;
                            });
                    } else {
                        addObj(this.formData)
                            .then(res => {
                                if (res.data.data) {
                                    this.backHistory();
                                }
                            })
                            .finally(() => {
                                this.saving = false;
                            });
                    }
                }
            });
        },
    },
    mounted() {}
};
