import { getUserList } from '../../../api/admin/user.js';
import { getStatisticsPage, getInvoices } from '../../../api/apply/invoice.js';
import { mapGetters } from 'vuex';
export default {
    data() {
        return {
            id: '',
            query: {
                userId: null,
                month: '',
                startTime: null,
                endTime: null,
                likeKeyWords: '',
                type1: '',
                orderBy: 'create_time_desc',
                current: 1,
                size: 10
            },
            pages: 0,
            total: 0,
            listLoading: false,
            userOptions: [],
            list: [],
            classifyOptions: [
                {
                    value: '',
                    label: '全部'
                }
            ]
        };
    },
    created() {
        this.getUserList();
        this.getStatisticsPage();
        this.id = this.userId;
        console.log(this.id);
        this.getInvoices('-1');
    },
    computed: {
        ...mapGetters(['permissions', 'userId'])
    },
    methods: {
        // 公司人员列表
        getUserList() {
            getUserList().then(response => {
                // console.log(response);
                response.data.data.forEach(element => {
                    //console.log(element)
                    this.userOptions.push({
                        value: element.userId,
                        label: element.username
                    });
                });
            });
        },

        // 获取报销统计
        getStatisticsPage() {
            this.listLoading = true;
            getStatisticsPage(this.query).then(res => {
                this.listLoading = false;
                this.total = res.data.data.total;
                this.query.current = res.data.data.current;
                this.query.size = res.data.data.size;
                this.list = res.data.data.records;
            });
        },

        getInvoices(id) {
            getInvoices(id, this.id).then(res => {
                console.log(res);
                res.data.data.invoiceType.forEach(item => {
                    this.classifyOptions.push({
                        value: item.id,
                        label: item.name
                    });
                });
            });
        },
        // 分页
        handleSizeChange(val) {
            this.query.size = val;
            this.getStatisticsPage();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            sessionStorage.setItem('page2', val);
            this.getStatisticsPage();
        }
    },
    mounted() {
        var date = new Date();
        var year = date.getFullYear();
        var month = date.getMonth() + 1;
        month = month > 9 ? month : '0' + month;
        var toMonth = year + '-' + month;
        this.query.month = toMonth;
    }
};
