import {
    getPageList,
    addUser,
    updateUser,
    deleteUser
} from "../../api/admin/user.js";
import { getTree } from "../../api/admin/dept.js";
import { deptRoleList } from "../../api/other/role.js";
import { mapGetters } from "vuex";
export default {
    data() {
        return {
            circleUrl:
                "https://cube.elemecdn.com/3/7c/3ea6beec64369c2642b92c6726f1epng.png",
            query: {
                username: "",
                deptId: "",
                current: 1,
                size: 10
            },
            pages: 0,
            total: 0,
            defaultImg: "",
            deptOptions: [],
            rolesOptions: [],
            pickerOptions: {
                disabledDate: time => {
                    return this.dealDisabledDate(time);
                }
            },
            dialogTitle: "",
            listLoading: false,
            dialogVisible: false,
            saving: false,
            list: [],
            user_btn_add: false,
            user_btn_edit: false,
            user_btn_del: false,
            formData: {
                newData: true,
                userId: "",
                companyId: "",
                username: "",
                password: "",
                deptId: "",
                sex: 0,
                phone: "",
                role: "",
                joinedTime: "",
                lockFlag: 0
            },
            rules: {
                username: [
                    { required: true, message: "请输入姓名", trigger: "blur" }
                ],
                deptId: [
                    { required: true, message: "请选择部门", trigger: "change" }
                ],
                sex: [
                    { required: true, message: "请选择性别", trigger: "blur" }
                ],
                phone: [
                    { required: true, message: "请输入手机号", trigger: "blur" }
                ],
                role: [
                    { required: true, message: "请选择角色", trigger: "change" }
                ],
                joinedTime: [
                    {
                        required: true,
                        message: "请选择入职日期",
                        trigger: "blur"
                    }
                ],
                lockFlag: [
                    { required: true, message: "请选择", trigger: "blur" }
                ],
            }
        };
    },
    created() {
        this.getDeptList();
        this.getRolelist();
        this.getUserList();
        this.user_btn_add = this.permissions["sys_user_add"];
        this.user_btn_edit = this.permissions["sys_user_edit"];
        this.user_btn_del = this.permissions["sys_user_del"];
    },
    computed: {
        ...mapGetters(["permissions"])
    },
    methods: {
        getDeptList() {
            getTree().then(response => {
                response.data.data.forEach(element => {
                    element.children.forEach(dept => {
                        this.deptOptions.push({
                            value: dept.id,
                            label: element.name + "-" + dept.name
                        });
                    });
                });
            });
        },
        getRolelist() {
            deptRoleList().then(response => {
                this.rolesOptions = response.data.data;
            });
        },
        getUserList() {
            this.loading = true;
            getPageList(this.query)
                .then(response => {
                    this.loading = false;
                    console.log(response.data.data)
                    this.total = response.data.data.total;
                    this.query.current = response.data.data.current;
                    this.query.size = response.data.data.size;
                    this.list = response.data.data.records;
                })
                .catch(() => {
                    this.loading = false;
                });
        },
        handleModal(data) {
            if (this.formData.phone.indexOf("*") > 0) {
                this.formData.phone = undefined;
            }
            console.log(data)
            if (data) {
                this.dialogTitle = "编辑员工 - " + data.username;
                this.formData.newData = false;
                this.formData.userId = data.userId;
                this.formData.companyId = data.companyId;
                this.formData.username = data.username;
                this.formData.password = "";
                this.formData.deptId = data.deptId;
                this.formData.sex = Number(data.sex);
                this.formData.phone = data.phone;
                this.formData.role = [];
                for (var i = 0; i < data.roleList.length; i++) {
                    this.formData.role[i] = data.roleList[i].roleId;
                }
                this.formData.joinedTime = data.joinedTime;
                this.formData.lockFlag = Number(data.lockFlag);
            } else {
                this.dialogTitle = "添加员工";
                this.formData.newData = true;
                this.formData.userId = "";
                this.formData.companyId = "";
                this.formData.username = "";
                this.formData.password = "";
                this.formData.deptId = "";
                this.formData.sex = 0;
                this.formData.phone = "";
                this.formData.role = [];
                this.formData.joinedTime = "2019-12-01";
                this.formData.lockFlag = 0;
            }
            this.dialogVisible = true;
        },
        // 单独处理时间的函数
        dealDisabledDate(time) {
            return time.getTime() >= Date.now();
        },
        handleSizeChange(val) {
            this.query.size = val;
            this.getUserList();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            this.getUserList();
        },
        formatSex: function(row) {
            return row.sex == 0 ? "男" : row.sex == 1 ? "女" : "未知";
        },
        selsChange: function(sels) {
            this.sels = sels;
        },
        handleSubmit() {
            this.$refs["formData"].validate(valid => {
                if (valid) {
                    this.saving = true;
                    if (!this.formData.newData) {
                        console.log(this.formData);
                        if (this.formData.phone.indexOf("*") != -1) {
                            this.formData.phone = "";
                        }
                        updateUser(this.formData)
                            .then(res => {
                                if (res.data.data) {
                                    this.dialogVisible = false;
                                    this.getUserList();
                                }
                            })
                            .finally(() => {
                                this.saving = false;
                            });
                    } else {
                        addUser(this.formData)
                            .then(res => {
                                if (res.data.data) {
                                    this.dialogVisible = false;
                                    this.getUserList();
                                }
                            })
                            .finally(() => {
                                this.saving = false;
                            });
                    }
                }
            });
        },
        // 删除
        handleDel(index, row) {
            if (row.userId) {
                this.$confirm("确认删除 " + row.username + " 吗?", "提示", {
                    type: "warning"
                }).then(() => {
                    deleteUser(row.userId).then(res => {
                        if (res.data.data) {
                            this.getUserList();
                        }
                    });
                });
            }
        }
    },
    mounted() {}
};
