import { getItemPartVosWithUserIdPage } from '../../api/project/team.js';
import { mapGetters } from 'vuex';
export default {
    data() {
        return {
            query: {
                likeKeyWords: '',
                type: '',
                userId: null,
                current: 1,
                size: 10
            },
            typeOptions: [
                {
                    value: '',
                    label: '全部'
                },
                {
                    value: '1',
                    label: '项目'
                },
                {
                    value: '2',
                    label: '产品'
                }
            ],
            pages: 0,
            total: 0,
            defaultImg: '',
            deptOptions: [],
            roleList: [
                {
                    value: '1',
                    label: '管理员'
                }
            ],
            listLoading: false,
            saving: false,
            list: [],
            item_btn_add: false,
            item_btn_edit: false,
            item_btn_del: false,
            formData: {
                newData: true,
                itemId: '',
                name: ''
            },
            rules: {
                name: [
                    {
                        required: true,
                        message: '请输入项目名称',
                        trigger: 'blur'
                    }
                ]
            }
        };
    },
    created() {
        this.getItemPartVosWithUserIdPage();
        this.query.userId = this.userId;
    },
    computed: {
        ...mapGetters(['permissions', 'userId'])
    },
    methods: {
        getItemPartVosWithUserIdPage() {
            this.listLoading = true;
            getItemPartVosWithUserIdPage(this.query)
                .then(response => {
                    this.listLoading = false;
                    //console.log(response.data.data)
                    this.total = response.data.data.total;
                    this.query.current = response.data.data.current;
                    this.query.size = response.data.data.size;
                    this.list = response.data.data.records;
                })
                .catch(() => {
                    this.listLoading = false;
                });
        },
        handleModalInfo(data) {
            window.localStorage.setItem('itemInfo', JSON.stringify(data));
            this.$router.push({
                path: '/project/itemInfo/' + data.itemId
            });
        },
        handleSizeChange(val) {
            this.query.size = val;
            this.getItemPartVosWithUserIdPage();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            this.getItemPartVosWithUserIdPage();
        }
    },
    mounted() {}
};
