// 表格导出
import FileSaver from 'file-saver';
import XLSX from 'xlsx';
import { getExpenseApproverPage } from '../../../api/apply/expense.js';
// import {getUserInfo} from "../../../api/admin/user.js";
import { mapGetters } from 'vuex';
export default {
    data() {
        return {
            query: {
                userId: null,
                likeKeyWords: '',
                status: '',
                current: 1,
                size: 10
            },
            statusOptions: [
                {
                    value: '',
                    label: '全部'
                },
                {
                    value: '0',
                    label: '审批中'
                },
                {
                    value: '1',
                    label: '同意'
                },
                {
                    value: '2',
                    label: '拒绝'
                }
            ],
            pages: 0,
            total: 0,
            listLoading: false,
            list: [],
            number: '',
            listType: '2'
        };
    },
    created() {
        window.localStorage.removeItem('editExpenseInfo');
        this.query.status = this.$route.query.status;
        if (!this.query.status) {
            this.query.status = '';
        }
        this.query.userId = this.userId;
        this.getExpenseApproverPage();
    },
    computed: {
        ...mapGetters(['permissions', 'userId'])
    },
    methods: {
        getExpenseApproverPage() {
            this.listLoading = true;
            getExpenseApproverPage(this.query)
                .then(response => {
                    this.listLoading = false;
                    this.total = response.data.data.total;
                    this.query.current = response.data.data.current;
                    this.query.size = response.data.data.size;
                    this.list = response.data.data.records;
                })
                .catch(() => {
                    this.listLoading = false;
                });
        },
        handleSizeChange(val) {
            this.query.size = val;
            this.getExpenseApproverPage();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            this.getExpenseApproverPage();
        },
        handInfo(data) {
            console.log(data);
            this.$router.push({
                path: '/apply/expense/info/' + data.expenseId
            });
        },
        openList(val) {
            if (val == 1) {
                this.$router.push({
                    path: '/apply/expense'
                });
            }
        },
        exportExcel() {
            let fix = document.querySelector('.el-table__fixed-right');
            let wb;
            if (fix) {
                //判断要导出的节点中是否有fixed的表格，如果有，转换excel时先将该dom移除，然后append回去
                wb = XLSX.utils.table_to_book(document.querySelector('#out-table').removeChild(fix));
                document.querySelector('#out-table').appendChild(fix);
            } else {
                wb = XLSX.utils.table_to_book(document.querySelector('#out-table'));
            }
            let wbout = XLSX.write(wb, { bookType: 'xlsx', bookSST: true, type: 'array' });
            try {
                FileSaver.saveAs(new Blob([wbout], { type: 'application/octet-stream' }), '我的支出审批列表.xlsx');
            } catch (e) {
                if (typeof console !== 'undefined') console.log(e, wbout);
            }
            return wbout;
        }
    },
    watch: {
        'query.likeKeyWords': {
            handler: function() {
                this.query.current = 1;
                this.getExpenseApproverPage();
            }
        }
    }
};
