import { dateFormat } from '../../utils/date.js';
import { getExpend } from '../../api/project/item.js';
import { mapGetters } from 'vuex';
export default {
    data() {
        return {
            query1: {
                startTime: '2020-01-01',
                endTime: dateFormat(new Date()),
                itemId: null
            },
            query2: {
                startTime: '2020-01-01',
                endTime: dateFormat(new Date()),
                itemId: null
            },
            //  柱状图
            optionBar: {
                tooltip: {
                    trigger: 'axis',
                    axisPointer: {
                        // 坐标轴指示器，坐标轴触发有效
                        type: 'shadow' // 默认为直线，可选为：'line' | 'shadow'
                    }
                },
                legend: {
                    data: []
                },
                grid: {
                    left: '3%',
                    right: '4%',
                    bottom: '3%',
                    containLabel: true
                },
                xAxis: {
                    type: 'value'
                },
                yAxis: [{ type: 'category', data: [] }],
                series: [
                    {
                        name: '',
                        type: 'bar',
                        stack: '总量',
                        label: {
                            show: true,
                            position: 'inside'
                        },
                        data: []
                    }
                ]
            },
            // 折线图
            optionLin: {
                title: {
                    text: '支出分类趋势'
                },
                tooltip: {
                    trigger: 'axis'
                },
                legend: {
                    data: []
                },
                grid: {
                    left: '3%',
                    right: '4%',
                    bottom: '3%',
                    containLabel: true
                },
                toolbox: {
                    feature: {
                        saveAsImage: {}
                    }
                },
                xAxis: [
                    {
                        type: 'category',
                        boundaryGap: false,
                        data: []
                    }
                ],
                yAxis: {
                    type: 'value'
                },
                series: [
                    {
                        name: '',
                        type: 'line',
                        stack: '总量',
                        data: []
                    }
                ]
            },
            // 饼图
            optionPie: {
                title: {
                    text: '支出占比',
                    // subtext: '纯属虚构',
                    x: 'center'
                },
                tooltip: {
                    trigger: 'item',
                    formatter: '{a} <br/>{b} : {c} ({d}%)'
                },
                legend: {
                    orient: 'vertical',
                    left: 'left',
                    data: []
                },
                series: [
                    {
                        name: '访问来源',
                        type: 'pie',
                        radius: '55%',
                        center: ['50%', '60%'],
                        data: [],
                        itemStyle: {
                            emphasis: {
                                shadowBlur: 10,
                                shadowOffsetX: 0,
                                shadowColor: 'rgba(0, 0, 0, 0.5)'
                            }
                        }
                    }
                ]
            }
        };
    },
    computed: {
        ...mapGetters(['permissions'])
    },
    created() {
        this.query1.itemId = this.$route.params.itemId;
        this.query2.itemId = this.$route.params.itemId;

        this.getItemExpend(this.query1);
        this.getItemExpend2(this.query2);
        this.getItemExpend3(this.query1);
    },
    mounted() {},
    methods: {
        getHourRadix() {
            this.optionBar.yAxis[0].data = [];
            this.optionBar.series[0].data = [];
            this.optionPie.series[0].data = [];
            this.getItemExpend(this.query1);
            this.getItemExpend3(this.query1);
        },
        getHourRadix2() {
            this.optionLin.xAxis[0].data = [];
            this.optionLin.series[0].data = [];
            this.getItemExpend2(this.query2);
        },

        // 柱状图
        getItemExpend(query) {
            getExpend(query).then(res => {
                console.log(res);
                res.data.data.forEach((item, index) => {
                    this.optionBar.yAxis[0].data[index] = item.createTime;
                    this.optionBar.series[0].data[index] = item.price;
                    if (item.type == null) {
                        this.optionBar.legend.data[index] = '其它';
                        this.optionBar.series[0].name = '其它';
                    }
                });
                this.drawBar();
            });

        },
        // 柱状
        drawBar() {
            let bar = this.$echarts.init(document.getElementById('bar'));
            bar.setOption(this.optionBar, (window.onresize = bar.resize));
            bar.resize();
            window.addEventListener('resize', function() {
                bar.resize();
            });
        },

        // 折线图
        getItemExpend2(query) {
            getExpend(query).then(res => {
                // console.log(res);
                res.data.data.forEach((item, index) => {
                    this.optionLin.xAxis[0].data[index] = item.createTime;
                    this.optionLin.series[0].data[index] = item.price;
                    if (item.type == null) {
                        this.optionLin.legend.data[index] = '其它';
                        this.optionLin.series[0].name = '其它';
                    }
                });
                this.drawLine();
            });
        },
        // 折线
        drawLine() {
            let line = this.$echarts.init(document.getElementById('line'));
            line.setOption(this.optionLin, (window.onresize = line.resize));
            line.resize();
            window.addEventListener('resize', function() {
                line.resize();
            });
        },
       
        // 饼状
        getItemExpend3(query){
            getExpend(query).then(res => {
                // console.log(res);
                res.data.data.forEach((item, index) => {
                    if (item.type == null) {
                        this.optionPie.legend.data[index] = '其它';
                        this.optionPie.series[0].data[index]={
                            value:item.price,
                            name:'其它'
                        }
                    }
                });
                this.drawPie();
            });
        },
        // 饼状
        drawPie() {
            let pie = this.$echarts.init(document.getElementById('pie'));
            pie.setOption(this.optionPie, (window.onresize = pie.resize));
            pie.resize();
            window.addEventListener('resize', function() {
                pie.resize();
            });
        },
    }
};
