import { addObj, editObj, getExpense } from '../../../api/apply/expense.js';
import { getApplyUserInfo } from '../../../api/admin/user.js';
import { getItemVosWithUserId } from '../../../api/project/team.js';
import { getSupplierAll } from '../../../api/customer/supplier.js';
import { getStandardApplyInfoById } from '../../../api/standard/apply.js';
import { mapGetters } from 'vuex';
export default {
    data() {
        return {
            query: {
                itemId: null,
                userId: null,
                deptId: null,
                isPublic: '1',
                type1: '',
                type2: '',
                itemNextId: null,
                type: 7,
                priceYuan: 0
            },
            fileList1: [],
            fileList2: [],
            formData: {
                newData: true,
                expenseId: null,
                name: '',
                type1: '',
                type2: '',
                type3: '',
                itemId: null,
                userId: '',
                isPublic: '1',
                isTackInvoice: '',
                invoiceType: '',
                supplierId: null,
                companyName: '',
                contact: '',
                buyUrl: '',
                model: '',
                bankName: '',
                bankAccount: '',
                num: null,
                unit: '',
                isLarge: '0',
                priceYuan: '',
                remark: '',
                invoiceNum: 1,
                detailed: '',
                invoiceImg: '',
                contract: '',
                arrivalTime: '',
                approverids: null,
                aliasNext: '',
                itemNextId: null
            },
            companyNameLable: '公司名称：',
            priceYuanLabel: '支出金额：',
            uploadUrl: '',
            itemOptions1: [],
            itemOptions2: [],
            supplierList: [],
            supplierOptions: [],
            rules: {
                name: [{ required: true, message: '请填写名称' }],
                type1: [
                    {
                        required: true,
                        message: '请选择总分类',
                        trigger: 'change'
                    }
                ],
                type2: [
                    {
                        required: true,
                        message: '请选择主分类',
                        trigger: 'change'
                    }
                ],
                type3: [
                    {
                        required: true,
                        message: '请选择明细分类',
                        trigger: 'change'
                    }
                ],
                itemId: [{ required: true, message: '请选择项目', trigger: 'change' }],
                isPublic: [
                    {
                        required: true,
                        message: '请选择是否对公',
                        trigger: 'change'
                    }
                ],
                companyName: [{ required: true, message: '请填写公司名称' }],
                contact: [{ required: true, message: '请填写联系方式' }],
                buyUrl: [{ required: true, message: '请填写购买链接' }],
                model: [{ required: true, message: '请填写型号参数' }],
                num: [{ required: true, message: '请填写购买数量' }],
                unit: [{ required: true, message: '请填写购买单位' }],
                bankName: [{ required: true, message: '请填写开户行名称' }],
                bankAccount: [{ required: true, message: '请填写开户行账号' }],
                isLarge: [
                    {
                        required: true,
                        message: '请选择金额比例',
                        trigger: 'change'
                    }
                ],
                priceYuan: [{ required: true, message: '请填写支出金额' }],
                remark: [{ required: true, message: '请填写备注信息' }],
                arrivalTime: [
                    {
                        required: true,
                        message: '请选择到货时间',
                        trigger: 'change'
                    }
                ],
                detailed: [{ required: true, message: '请填写用途及明细' }]
            },
            saving: false,
            applyUserList: [],
            sumClassifyOptions: [],
            mainClassifyOptions: [],
            subClassifyOptions: []
        };
    },
    created() {
        this.getStandardApplyInfo();
        this.getItemVosWithUserId(this.userId);
        this.getSupplierAll();
        this.formData.userId = this.userId;
        this.query.userId = this.userId;
        this.uploadUrl = `${window.location.origin}/apply/expense/upload`;

        const editPurchaseInfo = JSON.parse(window.localStorage.getItem('editPurchaseInfo'));
        console.log(editPurchaseInfo);
        if (editPurchaseInfo) {
            this.formData.name = editPurchaseInfo.name;
            this.formData.type1 = editPurchaseInfo.type1;
            this.formData.itemId = editPurchaseInfo.itemId;
            this.query.type1 = editPurchaseInfo.type1;
            this.query.companyId = editPurchaseInfo.companyId;
            this.query.itemId = editPurchaseInfo.itemId;
            this.query.itemNextId = editPurchaseInfo.itemNextId;
            this.applyUserList = editPurchaseInfo.checkUserList;
            this.query.type2 = editPurchaseInfo.type2;
            this.formData.type3 = editPurchaseInfo.type3;

            if (editPurchaseInfo.itemId != null && editPurchaseInfo.itemNextId == null) {
                this.formData.type2 = [editPurchaseInfo.type2, editPurchaseInfo.itemId];
            } else if (editPurchaseInfo.itemNextId != null) {
                this.formData.type2 = [editPurchaseInfo.type2, editPurchaseInfo.itemNextId, editPurchaseInfo.itemId];
            } else {
                this.formData.type2 = [editPurchaseInfo.type2];
            }
            getExpense(this.formData.type1, this.userId).then(res => {
                res.data.data.expenseType.forEach(item => {
                    this.mainClassifyOptions.push({
                        value: item.id,
                        label: item.name,
                        children: item.children
                    });
                });
            });
            getExpense(this.formData.type2[0], this.userId).then(res => {
                this.subClassifyOptions = res.data.data.expenseType;
            });


            if (editPurchaseInfo.pactImg) {
                editPurchaseInfo.pactImg.split(',').forEach((item, index) => {
                    if (item) {
                        this.fileList2.push({
                            url: `${window.location.origin}/apply/expense/` + item
                        });
                    }
                });
            }
            this.formData.bankAccount = editPurchaseInfo.bankAccount;
            this.formData.bankName = editPurchaseInfo.bankName;
            this.formData.companyName = editPurchaseInfo.companyName;
            this.formData.isPublic = editPurchaseInfo.isOpen;
        }

        const editExpenseInfo = JSON.parse(window.localStorage.getItem('editExpenseInfo'));
        console.log(editExpenseInfo);

        if (editExpenseInfo) {
            this.formData.newData = false;
            this.formData.expenseId = editExpenseInfo.expenseId;
            this.formData.name = editExpenseInfo.name;
            this.formData.classify = editExpenseInfo.classify;
            this.formData.type = editExpenseInfo.type;
            this.formData.itemId = editExpenseInfo.itemId;
            this.query.type1 = editExpenseInfo.type1;
            this.formData.type1 = editExpenseInfo.type1;
            this.query.companyId = editExpenseInfo.companyId;
            this.query.itemId = editExpenseInfo.itemId;
            this.query.itemNextId = editExpenseInfo.itemNextId;
            this.applyUserList = editExpenseInfo.checkUserList;
            this.query.type2 = editExpenseInfo.type2;
            if (editExpenseInfo.itemId != null && editExpenseInfo.itemNextId == null) {
                this.formData.type2 = [editExpenseInfo.type2, editExpenseInfo.itemId];
            } else if (editExpenseInfo.itemNextId != null) {
                this.formData.type2 = [editExpenseInfo.type2, editExpenseInfo.itemNextId, editExpenseInfo.itemId];
            } else {
                this.formData.type2 = [editExpenseInfo.type2];
            }

            getExpense(this.formData.type1, this.userId).then(res => {
                res.data.data.expenseType.forEach(item => {
                    this.mainClassifyOptions.push({
                        value: item.id,
                        label: item.name,
                        children: item.children
                    });
                });
            });
            getExpense(this.formData.type2[0], this.userId).then(res => {
                this.subClassifyOptions = res.data.data.expenseType;
            });
            this.formData.type3 = editExpenseInfo.type3;

            this.formData.isPublic = editExpenseInfo.isPublic;
            this.formData.isTackInvoice = editExpenseInfo.isTackInvoice;
            this.formData.invoiceType = editExpenseInfo.invoiceType;
            this.formData.supplierId = editExpenseInfo.supplierId;

            this.formData.companyName = editExpenseInfo.companyName;
            this.formData.contact = editExpenseInfo.contact;
            this.formData.buyUrl = editExpenseInfo.buyUrl;
            this.formData.model = editExpenseInfo.model;
            this.formData.num = editExpenseInfo.num;
            this.formData.unit = editExpenseInfo.unit;
            this.formData.bankName = editExpenseInfo.bankName;
            this.formData.bankAccount = editExpenseInfo.bankAccount;
            this.formData.isLarge = editExpenseInfo.isLarge;
            this.formData.priceYuan = editExpenseInfo.priceYuan;
            this.query.priceYuan = editExpenseInfo.priceYuan;
            this.formData.remark = editExpenseInfo.remark;
            this.formData.invoiceNum = editExpenseInfo.invoiceNum;
            this.formData.detailed = editExpenseInfo.detailed;
            this.formData.invoiceImg = editExpenseInfo.invoiceImg;
            this.formData.contract = editExpenseInfo.contract;
            this.formData.arrivalTime = editExpenseInfo.arrivalTime;
            if (editExpenseInfo.invoiceImg) {
                editExpenseInfo.invoiceImg.split(',').forEach((item, index) => {
                    if (item) {
                        this.fileList1.push({
                            url: `${window.location.origin}/apply/expense/` + item
                        });
                    }
                });
            }
            if (editExpenseInfo.contract) {
                editExpenseInfo.contract.split(',').forEach((item, index) => {
                    if (item) {
                        this.fileList2.push({
                            url: `${window.location.origin}/apply/expense/` + item
                        });
                    }
                });
            }

            this.getApplyUser();
        } else {
            this.getApplyUser();
        }

        this.getExpense('100000', this.userId);
    },
    computed: {
        ...mapGetters(['permissions', 'userId'])
    },
    methods: {
        getExpense(id, userId) {
            getExpense(id, userId).then(res => {
                this.sumClassifyOptions = res.data.data.expenseType;
            });
        },

        // 主分类
        classifyChange(val) {
            this.query.type1 = val;

            this.mainClassifyOptions = [];
            this.subClassifyOptions = [];

            getExpense(val, this.userId).then(res => {
                // console.log(res)
                res.data.data.expenseType.forEach(item => {
                    this.mainClassifyOptions.push({
                        value: item.id,
                        label: item.name,
                        children: item.children
                    });
                });
            });
        },

        // 明细分类
        classifyChange2(val) {
            this.query.itemId = null;
            this.formData.itemId = null;
            this.formData.itemNextId = null;
            getExpense(val[0], this.userId).then(res => {
                // console.log(res)
                this.subClassifyOptions = res.data.data.expenseType;
            });

            // 获取项目id
            if (val.length > 1 && val.length < 3) {
                this.query.itemId = val[1];
                this.formData.itemId = val[1];
                this.query.type2 = val[0];
                this.query.itemNextId = null;
                this.getApplyUser();
            } else if (val.length == 3) {
                this.query.type2 = val[0];
                this.query.itemId = val[2];
                this.formData.itemId = val[2];
                this.query.itemNextId = val[1];
                this.getApplyUser();
            } else {
                this.query.itemId = null;
                this.query.itemNextId = null;
                this.query.type2 = val[0];
                this.formData.itemId = null;
                this.getApplyUser();
            }
        },

        selectIsPublic(val) {
            this.query.isPublic = val;
            if (val == 0) {
                this.companyNameLable = '厂家名称：';
                this.priceYuanLabel = '议定单价：';
            }
            if (val == 1) {
                this.companyNameLable = '公司名称：';
                this.priceYuanLabel = '支出金额：';
            }
            this.getApplyUser();
        },

        getApplyUser() {
            this.query.priceYuan = this.formData.priceYuan;
            getApplyUserInfo(this.query).then(response => {
                console.log(response);
                this.applyUserList = [];
                this.formData.approverids = null;

                var newArr = response.data.data.filter(item => item.userId != this.query.userId);
                // 去重
                var newArr1 = [];
                var obj = {};
                for (var i = 0; i < newArr.length; i++) {
                    if (!obj[newArr[i].userId]) {
                        newArr1.push(newArr[i]);
                        obj[newArr[i].userId] = true;
                    }
                }
                newArr1.forEach(element => {
                    this.applyUserList.push({
                        userId: element.userId,
                        username: element.username,
                        avatar: element.avatar
                    });
                    if (this.formData.approverids == null) {
                        this.formData.approverids += element.userId;
                    } else {
                        this.formData.approverids += ',' + element.userId;
                    }
                });
            });
        },
        getItemVosWithUserId(userId) {
            getItemVosWithUserId(userId).then(response => {
                response.data.data.forEach(element => {
                    if (element.type == '1') {
                        this.itemOptions1.push({
                            value: element.itemId,
                            label: element.alias
                        });
                    }
                    if (element.type == '2') {
                        this.itemOptions2.push({
                            value: element.itemId,
                            label: element.alias
                        });
                    }
                });
            });
        },
        getSupplierAll() {
            getSupplierAll().then(response => {
                this.supplierList = response.data.data;
                response.data.data.forEach(element => {
                    this.supplierOptions.push({
                        supplierId: element.supplierId,
                        name: element.name
                    });
                });
            });
        },
        selectSupplier(val) {
            console.log(val);
            if (val != null && val != '') {
                var that = this;
                this.supplierList.forEach(element => {
                    if (element.supplierId == val) {
                        that.formData.name = element.name;
                        that.formData.bankName = element.bankName;
                        that.formData.bankAccount = element.bankAccount;
                    }
                });
            } else {
                this.formData.name = '';
                this.formData.bankName = '';
                this.formData.bankAccount = '';
            }
        },
        backHistory() {
            window.localStorage.removeItem('editExpenseInfo');
            window.localStorage.removeItem('editPurchaseInfo');
            this.formData.newData = true;
            this.formData.expenseId = null;
            this.formData.itemId = null;
            this.formData.isPublic = '1';
            this.formData.isTackInvoice = '';
            this.formData.invoiceType = '';
            this.formData.supplierId = null;
            this.formData.name = '';
            this.formData.bankName = '';
            this.formData.bankAccount = '';
            this.formData.isLarge = '0';
            this.formData.priceYuan = '';
            this.formData.invoiceNum = 1;
            this.formData.detailed = '';
            this.formData.invoiceImg = '';
            this.formData.contract = '';
            this.$router.go(-1);
        },
        onSubmit() {
            if (this.formData.type2.length > 2) {
                this.formData.itemNextId = this.formData.type2[1];
            }
            this.formData.type2 = this.formData.type2[0];
            console.log(this.formData);
            this.$refs['formData'].validate(valid => {
                if (valid) {
                    //console.log(this.formData)
                    // this.fileData = new FormData();
                    // this.$refs.upload2.submit();
                    this.saving = true;
                    if (!this.formData.newData) {
                        editObj(this.formData)
                            .then(res => {
                                if (res.data.data) {
                                    this.backHistory();
                                }
                            })
                            .finally(() => {
                                this.saving = false;
                            });
                    } else {
                        addObj(this.formData)
                            .then(res => {
                                if (res.data.data) {
                                    this.backHistory();
                                }
                            })
                            .finally(() => {
                                this.saving = false;
                            });
                    }
                }
            });
        },
        handleRemove1(data) {
            var serverUrl = `${window.location.origin}/apply/expense/`;
            const imgUrl = data.url.replace(serverUrl, '');
            this.formData.invoiceImg = this.formData.invoiceImg.replace(imgUrl + ',', '');
            console.log(this.formData.invoiceImg);
        },
        handlePreview1(file) {
            console.log(file);
        },
        handSuccess1(data) {
            const imgInfo = data.data;
            const imgUrl = imgInfo.bucketName + '-' + imgInfo.fileName;
            this.formData.invoiceImg += imgUrl + ',';
            console.log(this.formData.invoiceImg);
        },
        handleRemove2(data) {
            var serverUrl = `${window.location.origin}/apply/expense/`;
            const imgUrl = data.url.replace(serverUrl, '');
            this.formData.content = this.formData.contract.replace(imgUrl + ',', '');
            console.log(this.formData.contract);
        },
        handlePreview2(file) {
            console.log(file);
        },
        handSuccess2(data) {
            const imgInfo = data.data;
            const imgUrl = imgInfo.bucketName + '-' + imgInfo.fileName;
            this.formData.contract += imgUrl + ',';
            console.log(this.formData.contract);
        },
        getStandardApplyInfo() {
            getStandardApplyInfoById(7).then(response => {
                if (response.data.data) {
                    const data = response.data.data;
                    this.content = data.content;
                    const expenseStandardApply = JSON.parse(window.localStorage.getItem('expenseStandardApply'));
                    if (expenseStandardApply) {
                        if (expenseStandardApply.version < data.version) {
                            this.open();
                        }
                    } else {
                        this.open();
                    }
                    window.localStorage.setItem('expenseStandardApply', JSON.stringify(data));
                }
            });
        },
        open() {
            console.log(this.content);
            let content = this.content.split('\n\n');
            let newDatas = [];
            const h = this.$createElement;
            for (let i in content) {
                newDatas.push(h('p', null, content[i]));
            }
            this.$alert(h('div', null, newDatas), '支出规范');
        },
        beforeAvatarUpload: function(file) {
            const isJPG = file.type === 'image/jpeg';
            const isPNG = file.type === 'image/png';
            const isPG = isJPG || isPNG; //限制图片格式为jpg / png
            const isLt5M = file.size / 1024 / 1024 < 5; //限制图片大小
            if (!isPG) {
                this.$message.error('图片只能是 JPG 或 PNG 格式!');
            }
            if (!isLt5M) {
                this.$message.error('图片大小不能超过 5MB!');
            }
            return isPG && isLt5M;
        }
    },
    mounted() {}
};
