import { getHourRadixDept } from '../../../api/checkwork/hour.js';
import { dateFormat } from '../../../utils/date.js';
import { mapGetters } from 'vuex';
export default {
    data() {
        return {
            query: {
                startDay: dateFormat(new Date()).substr(0, 7) + '-01',
                endDay: dateFormat(new Date()),
                sortType: '1'
            },
            listLoading: false,
            list: [],
            optionBar: {
                color: ['#3398DB'],
                tooltip: {
                    trigger: 'axis',
                    axisPointer: {
                        // 坐标轴指示器，坐标轴触发有效
                        type: 'shadow' // 默认为直线，可选为：'line' | 'shadow'
                    }
                },
                grid: {
                    left: '3%',
                    right: '4%',
                    bottom: '3%',
                    containLabel: true
                },
                xAxis: [
                    {
                        type: 'category',
                        data: [],
                        axisTick: {
                            alignWithLabel: true
                        },
                        axisLabel: {
                            //底部文字倾斜
                            interval: 0,
                            rotate: 25
                        }
                    }
                ],
                yAxis: [
                    {
                        type: 'value'
                    }
                ],
                series: [
                    {
                        name: '部门系数',
                        type: 'bar',
                        barWidth: '40%',
                        data: [],
                        markLine: {
                            data: [
                                {
                                    type: 'average',
                                    name: '平均值',
                                    itemStyle: { normal: { color: '#dc143c' } }
                                }
                            ]
                        }
                    }
                ]
            }
        };
    },
    computed: {
        ...mapGetters(['permissions'])
    },
    created() {
        //const pageWidth = document.getElementById("divID").offsetWidth;
        //document.getElementById("bar").style =
        this.$nextTick(function() {
            this.getHourRadix();
        });
    },
    mounted() {},
    methods: {
        getHourRadix() {
            this.listLoading = true;
            getHourRadixDept(this.query)
                .then(response => {
                    this.listLoading = false;
                    this.list = response.data.data;
                    this.list.forEach((item, index) => {
                        //console.log(item)
                        this.optionBar.xAxis[0].data[index] = item.deptName;
                        this.optionBar.series[0].data[index] = item.radix;
                    });
                    this.drawBar();
                })
                .catch(() => {
                    this.listLoading = false;
                });
        },

        drawBar() {
            let bar = this.$echarts.init(document.getElementById('deptBar'));

            bar.setOption(this.optionBar, (window.onresize = bar.resize));
            window.addEventListener('resize', function() {
                bar.resize();
            });
        }
    }
};
