import {
    getFirstPartPage,
    addObj,
    editObj,
    deleteObj
} from "../../../api/customer/firstparty.js";
import { mapGetters } from "vuex";
export default {
    name: "ItemReceipt",
    data() {
        return {
            query: {
                itemId: null,
                likeKeyWords: "",
                current: 1,
                size: 10
            },
            formData: {
                newData: true,
                firstPartyId: null,
                itemId: null,
                name: "",
                address: "",
                telephone: "",
                dutyParagraph: "",
                bankName: "",
                bankAccount: ""
            },
            dialogTitle: "",
            dialogVisible: false,
            saving: false,
            rules: {
                name: [
                    {
                        required: true,
                        message: "请输入公司名称",
                        trigger: "blur"
                    }
                ],
                address: [
                    {
                        required: true,
                        message: "请输入公司注册地址",
                        trigger: "blur"
                    }
                ],
                telephone: [
                    {
                        required: true,
                        message: "请输入公司电话",
                        trigger: "blur"
                    }
                ],
                dutyParagraph: [
                    { required: true, message: "请输入税号", trigger: "blur" }
                ],
                bankName: [
                    {
                        required: true,
                        message: "请输入开户行名称",
                        trigger: "blur"
                    }
                ],
                bankAccount: [
                    {
                        required: true,
                        message: "请输入开户行账号",
                        trigger: "blur"
                    }
                ]
            },
            pages: 0,
            total: 0,
            listLoading: false,
            list: []
        };
    },
    created() {
        const itemId = this.$route.params.itemId;
        this.formData.itemId = itemId;
        this.query.itemId = itemId;
        this.getFirstPartPage();
    },
    computed: {
        ...mapGetters(["permissions"])
    },
    methods: {
        getFirstPartPage() {
            this.listLoading = true;
            getFirstPartPage(this.query)
                .then(response => {
                    this.listLoading = false;
                    this.total = response.data.data.total;
                    this.query.current = response.data.data.current;
                    this.query.size = response.data.data.size;
                    this.list = response.data.data.records;
                })
                .catch(() => {
                    this.listLoading = false;
                });
        },
        handleSizeChange(val) {
            this.query.size = val;
            this.getFirstPartPage();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            this.getFirstPartPage();
        },
        handleModal(data) {
            if (data) {
                this.dialogTitle = "编辑公司 - " + data.name;
                this.formData.newData = false;
                this.formData.firstPartyId = data.firstPartyId;
                this.formData.name = data.name;
                this.formData.address = data.address;
                this.formData.telephone = data.telephone;
                this.formData.dutyParagraph = data.dutyParagraph;
                this.formData.bankName = data.bankName;
                this.formData.bankAccount = data.bankAccount;
            } else {
                this.dialogTitle = "添加公司";
                this.formData.newData = true;
                this.formData.firstPartyId = "";
                this.formData.name = "";
                this.formData.address = "";
                this.formData.telephone = "";
                this.formData.dutyParagraph = "";
                this.formData.bankName = "";
                this.formData.bankAccount = "";
            }
            this.dialogVisible = true;
        },
        handleSubmit() {
            this.$refs["formData"].validate(valid => {
                if (valid) {
                    this.saving = true;
                    if (!this.formData.newData) {
                        console.log(this.formData);
                        editObj(this.formData)
                            .then(res => {
                                if (res.data.data) {
                                    this.dialogVisible = false;
                                    this.getFirstPartPage();
                                }
                            })
                            .finally(() => {
                                this.saving = false;
                            });
                    } else {
                        addObj(this.formData)
                            .then(res => {
                                if (res.data.data) {
                                    this.dialogVisible = false;
                                    this.getFirstPartPage();
                                }
                            })
                            .finally(() => {
                                this.saving = false;
                            });
                    }
                }
            });
        },
        handleDel(index, row) {
            if (row.companyId) {
                this.$confirm("确认删除吗?", "提示", {
                    type: "warning"
                }).then(() => {
                    deleteObj(row.companyId).then(res => {
                        if (res.data.data) {
                            this.getFirstPartPage();
                        }
                    });
                });
            }
        }
    }
};
