import {
    getClockInfo,
    getClockApproverList,
    updateClockApprover
} from "../../../api/apply/clock.js";
import { mapGetters } from "vuex";
export default {
    data() {
        return {
            formData: {
                approverId: null,
                checkUserId: null,
                check: "1",
                summary: "同意"
            },
            clockId: null,
            clockInfo: null,
            listLoading: false,
            list: [],
            approverList: [],
            rules: {
                summary: [{ required: true, message: "请填写原因" }]
            },
            showCheck: false,
            saving: false
        };
    },
    computed: {
        ...mapGetters(["permissions", "userId"])
    },
    created() {
        this.clockId = this.$route.params.clockId;
        this.formData.checkUserId = this.userId;
        this.getClockInfo();
        this.getClockApproverList();
    },
    methods: {
        getClockInfo() {
            getClockInfo(this.clockId).then(response => {
                this.clockInfo = response.data.data;
            });
        },
        getClockApproverList() {
            getClockApproverList(this.clockId).then(response => {
                this.approverList = response.data.data;
                this.approverList.forEach((item, index) => {
                    if (
                        item.check == 0 &&
                        item.userId == this.formData.checkUserId
                    ) {
                        this.formData.approverId = item.approverId;
                        this.showCheck = true;
                    }
                });
            });
        },
        backHistory() {
            this.$router.go(-1);
        },
        selectChcek() {
            if (this.formData.check == 1) {
                this.formData.summary = "同意";
            } else {
                this.formData.summary = "";
            }
        },
        onSubmit() {
            updateClockApprover(this.formData)
                .then(res => {
                    if (res.data.data) {
                        this.backHistory();
                    }
                })
                .finally(() => {
                    this.saving = false;
                });
        }
    }
};
