import { getReceipt } from '../../api/project/item.js';
import { getSupplierPage, addObj, editObj, deleteObj } from '../../api/customer/supplier.js';
import { mapGetters } from 'vuex';
export default {
    data() {
        return {
            itemId: '',
            query: {
                userId: null,
                status: '',
                current: 1,
                size: 10
            },
            pages: 0,
            total: 0,
            listLoading: false,
            list: [],
            number: '',
            listType: '1',
            proportion: 0
        };
    },
    computed: {
        ...mapGetters(['permissions'])
    },
    created() {
        this.itemId = this.$route.params.itemId;
        // console.log(this.itemId);
        // 获取开票信息列表
        this.getReceiptList();
    },
    mounted() {},
    methods: {
        handleSizeChange(val) {
            this.query.size = val;
            this.getReceiptPage();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            this.getReceiptPage();
        },

        // 获取开票信息列表
        getReceiptList() {
            getReceipt(this.itemId).then(res => {
                console.log(res);
                this.list = res.data.data;
                this.total = res.data.data.length;
                this.list.forEach(item => {
                    this.proportion += Number(item.proportion);
                });
                this.$nextTick(function() {
                    this.drawPie();
                });
            });
        },
        drawPie() {
            let pie = this.$echarts.init(document.getElementById('pie'));
            let optionPie = {
                series: [
                    {
                        center: ['25.0%', '50%'],
                        radius: ['49%', '50%'],
                        clockWise: false,
                        hoverAnimation: false,
                        type: 'pie',
                        itemStyle: {
                            normal: {
                                label: {
                                    show: true,
                                    textStyle: {
                                        fontSize: 15,
                                        fontWeight: 'bold'
                                    },
                                    position: 'center'
                                },
                                labelLine: {
                                    show: false
                                },
                                color: '#5886f0',
                                borderColor: '#5886f0',
                                borderWidth: 25
                            },
                            emphasis: {
                                label: {
                                    textStyle: {
                                        fontSize: 15,
                                        fontWeight: 'bold'
                                    }
                                },
                                color: '#5886f0',
                                borderColor: '#5886f0',
                                borderWidth: 25
                            }
                        },
                        data: [
                            {
                                value: this.proportion,
                                name: `已开票 ${this.proportion}%`
                            },
                            {
                                name: ' ',
                                value: 100 - this.proportion,
                                itemStyle: {
                                    normal: {
                                        label: {
                                            show: false
                                        },
                                        labelLine: {
                                            show: false
                                        },
                                        color: '#5886f0',
                                        borderColor: '#5886f0',
                                        borderWidth: 0
                                    },
                                    emphasis: {
                                        color: '#5886f0',
                                        borderColor: '#5886f0',
                                        borderWidth: 0
                                    }
                                }
                            }
                        ]
                    },
                    {
                        center: ['75.0%', '50%'],
                        radius: ['49%', '50%'],
                        clockWise: false,
                        hoverAnimation: false,
                        type: 'pie',
                        itemStyle: {
                            normal: {
                                label: {
                                    show: true,
                                    textStyle: {
                                        fontSize: 15,
                                        fontWeight: 'bold'
                                    },
                                    position: 'center'
                                },
                                labelLine: {
                                    show: false
                                },
                                color: '#ee3a3a',
                                borderColor: '#ee3a3a',
                                borderWidth: 25
                            },
                            emphasis: {
                                label: {
                                    textStyle: {
                                        fontSize: 15,
                                        fontWeight: 'bold'
                                    }
                                },
                                color: '#ee3a3a',
                                borderColor: '#ee3a3a',
                                borderWidth: 25
                            }
                        },
                        data: [
                            {
                                value: 100 - this.proportion,
                                name: `未开票 ${100 - this.proportion}%`
                            },
                            {
                                name: ' ',
                                value: this.proportion,
                                itemStyle: {
                                    normal: {
                                        label: {
                                            show: false
                                        },
                                        labelLine: {
                                            show: false
                                        },
                                        color: '#ee3a3a',
                                        borderColor: '#ee3a3a',
                                        borderWidth: 0
                                    },
                                    emphasis: {
                                        color: '#ee3a3a',
                                        borderColor: '#ee3a3a',
                                        borderWidth: 0
                                    }
                                }
                            }
                        ]
                    }
                ]
            };
            pie.setOption(optionPie, (window.onresize = pie.resize));
        }
    }
};
