import { getUserList } from '../../../api/admin/user.js';
import { getPurchaseExecutePage, getPurchase } from '../../../api/purchase/purchase.js';
import { mapGetters } from 'vuex';
export default {
    data() {
        return {
            query: {
                userId: null,
                principalId: null,
                status: '',
                likeKeyWords: '',
                current: 1,
                size: 10,
                type1: ''
            },
            list: [],
            pages: 0,
            total: 0,
            disabled: false,
            listLoading: false,
            statusOptions: [
                {
                    value: '',
                    label: '全部'
                },
                {
                    value: '0',
                    label: '审批中'
                },
                {
                    value: '1',
                    label: '同意'
                },
                {
                    value: '2',
                    label: '拒绝'
                }
            ],
            classifyOptions: [
                {
                    value: '',
                    label: '全部'
                }
            ],
            userOptions: []
        };
    },
    computed: {
        ...mapGetters(['permissions', 'userId'])
    },
    created() {
        this.query.principalId = this.userId;
        let page = sessionStorage.getItem('page2');
        if (page != null) {
            this.query.current = Number(page);
        }
        // this.query.userId=this.userId;
        this.getUserList();
        this.getPurchaseExecutePage();
        this.getPurchase('100000');
    },

    methods: {
        getPurchase(id) {
            getPurchase(id, this.userId).then(res => {
                res.data.data.purchaseType.forEach(item => {
                    this.classifyOptions.push({
                        value: item.id,
                        label: item.name
                    });
                });
            });
        },
        getPurchaseExecutePage() {
            getPurchaseExecutePage(this.query).then(res => {
                console.log(res);
                this.total = res.data.data.total;
                this.query.current = res.data.data.current;
                this.query.size = res.data.data.size;
                this.list = res.data.data.records;
            });
        },
        handleSizeChange(val) {
            this.query.size = val;
            this.getPurchaseExecutePage();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            sessionStorage.setItem('page2', val);
            this.getPurchaseExecutePage();
        },

        getUserList() {
            getUserList().then(response => {
                console.log(response);
                response.data.data.forEach(element => {
                    //console.log(element)
                    this.userOptions.push({
                        value: element.userId,
                        label: element.username
                    });
                });
            });
        },

        lookModal(val) {
            this.$router.push({
                path: '/purchase/Info/' + val.purchaseId
            });
        },
        handleModal(val) {
            this.$router.push({
                path: '/purchase/Edit',
                query: {
                    purchaseId: val.purchaseId
                }
            });
        }
    }
};
