import { getClockApproverPage } from '../../../api/apply/clock.js';
// import {getUserInfo} from "../../../api/admin/user.js";
import { mapGetters } from 'vuex';
export default {
    data() {
        return {
            query: {
                userId: null,
                status: '',
                current: 1,
                size: 10
            },
            statusOptions: [
                {
                    value: '',
                    label: '全部'
                },
                {
                    value: '0',
                    label: '审批中'
                },
                {
                    value: '1',
                    label: '同意'
                },
                {
                    value: '2',
                    label: '拒绝'
                }
            ],
            pages: 0,
            total: 0,
            listLoading: false,
            list: [],
            number: '',
            listType: '2'
        };
    },
    created() {
        window.localStorage.removeItem('editClockInfo');
        this.query.status = this.$route.query.status;
        if (!this.query.status) {
            this.query.status = '';
        }
        this.query.userId = this.userId;
        this.getClockApproverPage();
    },
    computed: {
        ...mapGetters(['permissions', 'userId'])
    },
    methods: {
        getClockApproverPage() {
            this.listLoading = true;
            getClockApproverPage(this.query)
                .then(response => {
                    this.listLoading = false;
                    this.total = response.data.data.total;
                    this.query.current = response.data.data.current;
                    this.query.size = response.data.data.size;
                    this.list = response.data.data.records;
                    this.list.forEach((item, index) => {
                        const clockInfo = JSON.parse(item.clockList);
                        this.list[index].clockDay = clockInfo.day;
                        this.list[index].clockTime = clockInfo.time;
                        this.list[index].clockStatus = clockInfo.status;
                    });
                })
                .catch(() => {
                    this.listLoading = false;
                });
        },
        handleSizeChange(val) {
            this.query.size = val;
            this.getLoglist();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            this.getLoglist();
        },
        handleInfo(data) {
            this.$router.push({
                path: '/apply/clock/info/' + data.clockId
            });
        },
        openList(val) {
            if (val == 1) {
                this.$router.push({
                    path: '/apply/clock'
                });
            }
        }
    },
    mounted() {}
};
