import { getPageList, syncTemplate } from "../../api/weixin/template.js";
import { getAccountList } from "../../api/weixin/account.js";
import { mapGetters } from "vuex";
export default {
    data() {
        return {
            query: {
                appid: null,
                current: 1,
                size: 10
            },
            pages: 0,
            total: 0,
            defaultImg: "",
            dialogTitle: "",
            listLoading: false,
            dialogVisible: false,
            saving: false,
            list: [],
            wx_fans_btn_add: false,
            wx_fans_btn_edit: false,
            wx_fans_btn_del: false,
            accountOptions: []
        };
    },
    created() {
        this.getAccountList();
        this.getPageList();
        this.wx_fans_btn_add = this.permissions["wx_fans_add"];
        this.wx_fans_btn_edit = this.permissions["wx_fans_edit"];
        this.wx_fans_btn_del = this.permissions["wx_fans_del"];
    },
    computed: {
        ...mapGetters(["permissions"])
    },
    methods: {
        getAccountList() {
            getAccountList().then(response => {
                response.data.data.forEach(element => {
                    this.accountOptions.push({
                        value: element.appid,
                        label: element.name
                    });
                });
            });
        },
        getPageList() {
            this.loading = true;
            getPageList(this.query)
                .then(response => {
                    this.loading = false;
                    //console.log(response.data.data)
                    this.total = response.data.data.total;
                    this.query.current = response.data.data.current;
                    this.query.size = response.data.data.size;
                    this.list = response.data.data.records;
                })
                .catch(() => {
                    this.loading = false;
                });
        },
        // 单独处理时间的函数
        dealDisabledDate(time) {
            return time.getTime() >= Date.now();
        },
        handleSizeChange(val) {
            this.query.size = val;
            this.getPageList();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            this.getPageList();
        },
        formatSex: function(row) {
            return row.sex == 0 ? "男" : row.sex == 1 ? "女" : "未知";
        },
        selsChange: function(sels) {
            this.sels = sels;
        },

        syncTemplate() {
            if (!this.query.appid) {
                this.$message.error("请选择账号");
                return false;
            }
            this.$confirm("确认 同步 吗?", "提示", {
                type: "warning"
            }).then(() => {
                syncTemplate(this.query.appid).then(res => {
                    if (res.data.data) {
                        this.getPageList();
                    }
                });
            });
        }
    },
    mounted() {}
};
