import { fetchList, putObj } from '../../../api/checkwork/month.js';
import { getUserList } from '../../../api/admin/user.js';
import { mapGetters } from 'vuex';
export default {
    data() {
        return {
            query: {
                userId: '',
                current: 1,
                size: 10
            },
            total: 0,
            dialogTitle: '',
            listLoading: false,
            dialogVisible: false,
            saving: false,
            list: [],
            pickerOptions: {
                disabledDate: time => {
                    return this.dealDisabledDate(time);
                }
            },
            formData: {
                numId: null,
                userName: '',
                month: '',
                clockNum: 0,
                lateNum: 0
            },
            dd_month_edit: false,
            rules: {
                name: [{ required: true, message: '请输入名称' }],
                day: [{ required: true, message: '请选择日期', trigger: 'change' }],
                type: [{ required: true, message: '请选择类型', trigger: 'change' }]
            },
            userOptions: []
        };
    },
    created() {
        this.getUserList();
        this.getMonthList();
        this.query.userId = this.userId;
        this.dd_month_edit = this.permissions['dd_month_edit'];
    },
    computed: {
        ...mapGetters(['permissions', 'userId'])
    },
    methods: {
        getMonthList() {
            this.listLoading = true;
            fetchList(this.query)
                .then(response => {
                    console.log(response);
                    this.loading = false;
                    this.listLoading = false;
                    this.total = response.data.data.total;
                    this.query.current = response.data.data.current;
                    this.query.size = response.data.data.size;
                    this.list = response.data.data.records;
                })
                .catch(() => {
                    this.listLoading = false;
                });
        },

        getUserList() {
            getUserList().then(response => {
                response.data.data.forEach(element => {
                    //console.log(element)
                    this.userOptions.push({
                        value: element.userId,
                        label: element.username
                    });
                });
            });
        },
        handleModal(data) {
            if (data) {
                this.dialogTitle = '编辑假期 - ' + data.day;
                this.formData.newData = false;
                this.formData.numId = data.numId;
                this.formData.userName = data.userName;
                this.formData.month = data.month;
                this.formData.clockNum = data.clockNum;
                this.formData.lateNum = data.lateNum;
                this.dialogVisible = true;
            }
        },
        handleSubmit() {
            this.$refs['formData'].validate(valid => {
                if (valid) {
                    this.saving = true;
                    if (!this.formData.newData) {
                        putObj(this.formData)
                            .then(res => {
                                if (res.data.data) {
                                    this.dialogVisible = false;
                                    this.getMonthList();
                                }
                            })
                            .finally(() => {
                                this.saving = false;
                            });
                    }
                }
            });
        },
        // 删除
        handleDel(index, row) {
            if (row.id) {
                this.$confirm('确认删除 ' + row.day + ' 吗?', '提示', {
                    type: 'warning'
                }).then(() => {
                    delObj(row.id).then(res => {
                        if (res.data.data) {
                            this.getMonthList();
                        }
                    });
                });
            }
        },
        // 单独处理时间的函数
        dealDisabledDate(time) {
            return time.getTime() <= Date.now();
        },
        handleSizeChange(val) {
            this.query.size = val;
            this.getMonthList();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            this.getMonthList();
        }
    },
    mounted() {}
};
