import {
    getTree,
    addDept,
    updateDept,
    deleteDept
} from "../../api/admin/dept.js";
import { getUserList } from "../../api/admin/user.js";
import { mapGetters } from "vuex";
export default {
    data() {
        return {
            dialogTitle: "",
            listLoading: false,
            dialogVisible: false,
            saving: false,
            list: [],
            formData: {
                deptId: "",
                deptName: "",
                userId: "",
                userName: "",
                sort: ""
            },
            userOptions: [],
            dept_btn_add: false,
            dept_btn_edit: false,
            dept_btn_del: false,
            number: "",
            width: {
                type: String,
                default: "80%"
            },
            rules: {
                deptName: [
                    { required: true, message: "请输入名称", trigger: "blur" }
                ],
                userId: [
                    {
                        required: true,
                        message: "请选择负责人",
                        trigger: "change"
                    }
                ],
                sort: [
                    { required: true, message: "请输入排序", trigger: "blur" }
                ]
            }
        };
    },
    created() {
        this.getUserList();
        this.getDepartList();
        this.dept_btn_add = this.permissions["sys_dept_add"];
        this.dept_btn_edit = this.permissions["sys_dept_edit"];
        this.dept_btn_del = this.permissions["sys_dept_del"];
    },
    computed: {
        ...mapGetters(["permissions"])
    },
    methods: {
        getUserList() {
            getUserList().then(response => {
                response.data.data.forEach(element => {
                    //console.log(element)
                    this.userOptions.push({
                        value: element.userId,
                        label: element.username
                    });
                });
            });
        },
        getDepartList() {
            this.loading = true;
            getTree(null)
                .then(response => {
                    this.loading = false;
                    this.list = response.data.data;
                    this.number = this.list.length;
                })
                .catch(() => {
                    this.loading = false;
                });
        },

        handleModal(data) {
            if (data) {
                this.dialogTitle = "编辑部门 - " + data.name;
                this.formData.newData = false;
                this.formData.deptId = data.id;
                this.formData.deptName = data.name;
                this.formData.userId = data.userId;
                this.formData.userName = data.userName;
                this.formData.sort = data.sort;
            } else {
                this.dialogTitle = "添加部门";
                this.formData.newData = true;
                this.formData.userId = "";
                this.formData.deptName = "";
                this.formData.userId = "";
                this.formData.userName = "";
                this.formData.sort = "";
            }
            this.dialogVisible = true;
        },
        handleSubmit() {
            this.$refs["formData"].validate(valid => {
                if (valid) {
                    this.saving = true;
                    if (!this.formData.newData) {
                        updateDept(this.formData)
                            .then(res => {
                                if (res.data.data) {
                                    this.dialogVisible = false;
                                    this.getDepartList();
                                }
                            })
                            .finally(() => {
                                this.saving = false;
                            });
                    } else {
                        addDept(this.formData)
                            .then(res => {
                                if (res.data.data) {
                                    this.dialogVisible = false;
                                    this.getDepartList();
                                }
                            })
                            .finally(() => {
                                this.saving = false;
                            });
                    }
                }
            });
        },
        // 删除
        handleDel(index, row) {
            if (row.deptId) {
                this.$confirm("确认删除 " + row.deptName + " 吗?", "提示", {
                    type: "warning"
                }).then(() => {
                    deleteDept(row.deptId).then(res => {
                        if (res.data.data) {
                            this.getDepartList();
                        }
                    });
                });
            }
        }
    },
    mounted() {}
};
