import { getPageList, loadClockDay } from '../../../api/checkwork/hour.js';
import { getUserList } from '../../../api/admin/user.js';
import { dateFormat } from '../../../utils/date.js';
import { mapGetters } from 'vuex';
export default {
    data() {
        return {
            query: {
                selectMonth: dateFormat(new Date()).substr(0, 7),
                userId: '',
                current: 1,
                size: 10
            },
            userOptions: [],
            expireTimeOption: {
                disabledDate: time => {
                    var date = new Date();
                    var year = date.getFullYear();
                    var month = date.getMonth() + 1;
                    if (month >= 1 && month <= 9) {
                        month = '0' + month;
                    }
                    var currentdate = year.toString() + month.toString();
                    var timeyear = time.getFullYear();
                    var timemonth = time.getMonth() + 1;
                    if (timemonth >= 1 && timemonth <= 9) {
                        timemonth = '0' + timemonth;
                    }
                    var timedate = timeyear.toString() + timemonth.toString();
                    return currentdate < timedate;
                }
            },
            pages: 0,
            total: 0,
            listLoading: false,
            list: [],
            number: '',
            checkTotal: [
                {
                    count: '1',
                    normal: '1',
                    late: '1',
                    early: '1',
                    no: '1',
                    over: '1',
                    business: '1',
                    leave: '1',
                    out: '1'
                }
            ],
            userStatisticsInfo: {
                userName: ''
            }
        };
    },
    created() {
        this.getUserList();
        this.getClockList();
        this.query.userId = this.userId;
    },
    computed: {
        ...mapGetters(['permissions', 'userId'])
    },
    methods: {
        open() {},
        getClockList() {
            this.listLoading = true;
            getPageList(this.query)
                .then(response => {
                    this.listLoading = false;
                    this.total = response.data.data.total;
                    this.query.current = response.data.data.current;
                    this.query.size = response.data.data.size;
                    this.list = response.data.data.records;
                    console.log(this.list);
                })
                .catch(() => {
                    this.listLoading = false;
                });
        },
        getUserList() {
            getUserList().then(response => {
                console.log(response);
                response.data.data.forEach(element => {
                    //console.log(element)
                    this.userOptions.push({
                        value: element.userId,
                        label: element.username
                    });
                });
            });
        },
        handUpdate(row) {
            loadClockDay(row.userId, row.day).then(response => {
                this.getClockList();
            });
        },
        formatType: function(row) {
            return row.type == 1 ? '上班' : row.type == 2 ? '下班' : '未知';
        },
        // 单独处理时间的函数
        dealDisabledDate(time) {
            return time.getTime() >= Date.now();
        },
        handleSizeChange(val) {
            this.query.size = val;
            this.getClockList();
        },
        handleCurrentChange(val) {
            this.query.current = val;
            this.getClockList();
        }
    },
    mounted() {}
};
