export default {
  name: 'PlxTableLoading',
  props: {
    visible: Boolean
  },
  render (h) {
    return h('div', {
      class: 'plx-table--loading',
      style: {
        display: this.visible ? 'block' : 'none'
      }
    }, [
      h('div', {
        class: 'plx-table--spinner'
      })
    ])
  }
}
